    private void jBcadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBcadastrarActionPerformed
        Paciente a = new Paciente();
        
        //dados pessoais        
        a.setNome(jTnome.getText());
        a.setNome_social(jTnome_social.getText());
        a.setRg(parseInt(jTrg.getText()));
        a.setCpf(parseInt(jTcpf.getText()));
        a.setSexo(jTsexo.getText());
        a.setNascimento(jTnascimento.getText());
        a.setTelefone(parseInt(jTtelefone.getText())); 
        
        //endereco
        a.setRua(jTrua.getText());    
        a.setNumero(parseInt(jTnumero.getText()));
        a.setBairro(jTbairro.getText());
        a.setCidade(jTcidade.getText());    
        a.setUf(jTuf.getText());
        
        ManterPaciente dao = new ManterPaciente();
        
        try {
            dao.inserir(a);
            jTnome.setText("");
            jTnome_social.setText("");
            jTrg.setText("");
            jTcpf.setText("");
            jTsexo.setText("");
            jTnascimento.setText("");
            jTtelefone.setText("");
            jTrua.setText("");
            jTnumero.setText("");
            jTbairro.setText("");
            jTcidade.setText("");
            jTuf.setText("");           
            jTnome.requestFocus();
        } catch (Exception ex) {
            Logger.getLogger(PacienteForm.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_jBcadastrarActionPerformed

